import React, { Component, PropTypes } from 'react';
import {
  AppRegistry,
  StyleSheet,
  Text,
  View,
  NavigatorIOS,
  TouchableHighlight,
  TabBarIOS
} from 'react-native';
import client from './App/store'
import { ApolloProvider } from 'react-apollo';
import HomeView from './App/HomeView'

var base64Icon = 'data:image/png;base64,';

class Project extends Component {
  static propTypes = {
    title: PropTypes.string.isRequired,
    navigator: PropTypes.object.isRequired,
  }
  
  state = {
    selectedTab: 'blueTab',
    notifCount: 0,
    presses: 0,
  };

  constructor(props, context) {
    super(props, context);
    this._onForward = this._onForward.bind(this);
    this.nextIndex = 1;
  }
  
  _renderContent = (color, pageText, num) => {
    return (
      <View style={[styles.tabContent, {backgroundColor: color}]}>
        <Text style={styles.tabText}>{pageText}</Text>
        <Text style={styles.tabText}>{num} re-renders of the {pageText}</Text>
      </View>
    );
  };

  _onForward() {
    this.props.navigator.push({
      title: 'Scene ' + this.nextIndex++,
      component: Project,
    });
  }
  render() {
    return (
      <TabBarIOS
        unselectedTintColor="black"
        tintColor="white"
        barTintColor="#ff6600">
        <TabBarIOS.Item
          title="System Summary"
          icon={{uri: base64Icon, scale: 3}}
          selected={this.state.selectedTab === 'blueTab'}
          onPress={() => {
            this.setState({
              selectedTab: 'blueTab',
            });
          }}>
          {this._renderContent('#414A8C', 'Blue Tab')}
        </TabBarIOS.Item>
        <TabBarIOS.Item
          title="Status"
          systemIcon="contacts"
          badge={this.state.notifCount > 0 ? this.state.notifCount : undefined}
          selected={this.state.selectedTab === 'redTab'}
          onPress={() => {
            this.setState({
              selectedTab: 'redTab',
              notifCount: this.state.notifCount + 1,
            });
          }}>
          {this._renderContent('#783E33', 'Red Tab', this.state.notifCount)}
        </TabBarIOS.Item>
        <TabBarIOS.Item
          icon={require('./ios/static/checklist.imageset/ic_playlist_add_check.png')}
          selectedIcon={require('./ios/static/checklist.imageset/ic_playlist_add_check.png')}
          renderAsOriginal
          title="Status"
          selected={this.state.selectedTab === 'greenTab'}
          onPress={() => {
            this.setState({
              selectedTab: 'greenTab',
              presses: this.state.presses + 1
            });
          }}>
          {this._renderContent('#21551C', 'Green Tab', this.state.presses)}
        </TabBarIOS.Item>
      </TabBarIOS>
    );
  }
}

class App extends Component {
  render() {
    return (      
      <ApolloProvider client={client}>
        <HomeView />
      </ApolloProvider>
    )
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F5FCFF',
  },
  welcome: {
    fontSize: 20,
    textAlign: 'center',
    margin: 10,
  },
  instructions: {
    textAlign: 'center',
    color: '#333333',
    marginBottom: 5,
  },
});

AppRegistry.registerComponent('Project', () => App);
